    removeDataset(id: string): Promise<InsightResponse> {

        let retInsight:InsightResponse={
            code:null,
            body:{}
        };

        return new Promise(function (fulfill, reject) {

            // if(id!=="courses"){
            //     retInsight.code = 404;
            //     return reject(retInsight);
            // }
            if(fs.existsSync(id)) {
                fs.unlinkSync(id);
                retInsight.code = 204;
                fulfill(retInsight);
            }else{
                retInsight.code = 404;
                reject(retInsight);
            }
        })
    }

